import React, { useState, useEffect } from 'react';
import axios from 'axios';

function BookManagement() {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({
    title: '',
    author: '',
    publishedYear: '',
  });

  useEffect(() => {
    // Fetch books from the backend when the component mounts
    axios.get('http://localhost:3001/books')
      .then(response => setBooks(response.data))
      .catch(error => console.error('Error fetching books:', error));
  }, []);

  const addBook = () => {
    axios.post('http://localhost:3001/books/add', newBook)
      .then(response => setBooks([...books, response.data]))
      .catch(error => console.error('Error adding book:', error));

    setNewBook({
      title: '',
      author: '',
      publishedYear: '',
    });
  };

  return (
    <div>
      <h1>Book Management</h1>
      <ul>
        {books.map(book => (
          <li key={book.id}>
            <strong>{book.title}</strong> by {book.author}, published in {book.publishedYear}
          </li>
        ))}
      </ul>
      <div>
        <input
          type="text"
          placeholder="Title"
          value={newBook.title}
          onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Author"
          value={newBook.author}
          onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
        />
        <input
          type="text"
          placeholder="Published Year"
          value={newBook.publishedYear}
          onChange={(e) => setNewBook({ ...newBook, publishedYear: e.target.value })}
        />
        <button onClick={addBook}>Add Book</button>
      </div>
    </div>
  );
}

export default BookManagement;
