import React, { useState, useEffect } from 'react';
import axios from 'axios';

function BookManagement() {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({
    title: '',
    author: '',
    publishedYear: '',
  });

  useEffect(() => {
    // Fetch books from the backend when the component mounts
    axios.get('http://localhost:3005/books')
      .then(response => setBooks(response.data))
      .catch(error => console.error('Error fetching books:', error));
  }, []);

  const addBook = () => {
    axios.post('http://localhost:3005/books', newBook)
      .then(response => setBooks([...books, response.data]))
      .catch(error => console.error('Error adding book:', error));

    setNewBook({
      title: '',
      author: '',
      publishedYear: '',
    });
  };

  const updateBook = (bookId, updatedInfo) => {
    axios.put(`http://localhost:3005/books/${bookId}`, updatedInfo)
      .then(response => {
        const updatedBooks = books.map(book => {
          if (book.id === bookId) {
            return { ...book, ...updatedInfo };
          }
          return book;
        });
        setBooks(updatedBooks);
      })
      .catch(error => console.error('Error updating book:', error));
  };

  const deleteBook = (bookId) => {
    axios.delete(`http://localhost:3005/books/${bookId}`)
      .then(response => {
        const updatedBooks = books.filter(book => book.id !== bookId);
        setBooks(updatedBooks);
      })
      .catch(error => console.error('Error deleting book:', error));
  };

  return (
    <div>
      <h1>Book Management</h1>
      <ul>
        {books.map(book => (
          <li key={book.id}>
            <strong>{book.title}</strong> by {book.author}, published in {book.publishedYear}
            <button onClick={() => updateBook(book.id, { title: 'Updated Title' })}>Update</button>
            <button onClick={() => deleteBook(book.id)}>Delete</button>
          </li>
        ))}
      </ul>
      <div>
        <input
          type="text"
          placeholder="Title"
          value={newBook.title}
          onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Author"
          value={newBook.author}
          onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
        />
        <input
          type="text"
          placeholder="Published Year"
          value={newBook.publishedYear}
          onChange={(e) => setNewBook({ ...newBook, publishedYear: e.target.value })}
        />
        <button onClick={addBook}>Add Book</button>
      </div>
    </div>
  );
}

export default BookManagement;
